[example.c] void bubble_sort(int* arr, int size){
    size--;
    while(size){
        for(int i=0, j=i+1; i<size, j<=size; i++, j++){
            //swap
            if(arr[i] > arr[j]){
                int tmp = arr[i];
                arr[i] = arr[j];
                arr[j] = tmp;
            }
        }
        size--;
    }
}

