[example.c] void selection_sort(int* arr, int size){
    for(int i=0; i<size-1; i++){
        int min_pos = i;
        for(int j=i+1; j<size; j++){
            if(arr[j] < arr[min_pos])
                min_pos = j;
        }
        //swap
        int tmp = arr[min_pos];
        arr[min_pos] = arr[i];
        arr[i] = tmp;
    }
}
